import React, { useState } from "react";
import Navbar from "../components/Navbar";
import "./APTG.css";

function APTG() {
  const [selectedMainCategory, setSelectedMainCategory] = useState(null);
  const [selectedSubCategory, setSelectedSubCategory] = useState(null);

  const mainCategories = [
    "Category 1",
    "Category 2",
    "Category 3",
    "Category 4",
    "Category 5",
    "Category 6",
    "Category 7",
    "Category 8",
  ];

  const subCategories = {
    "Category 1": [
      { name: "Sub 1.1", content: "Nandyal Town Top 5 Grossers" },
      { name: "Sub 1.2" },
      { name: "Sub 1.3" },
      { name: "Sub 1.4" },
      { name: "Sub 1.5" },
    ],
    // Add other categories similarly...
  };

  const topGrossers = [
    { name: "#RRR", gross: "2,38,75,000" },
    { name: "#Pushpa2", gross: "1,88,25,385" },
    { name: "#Devara", gross: "1,58,08,320" },
    { name: "#Baahubali2", gross: "1,28,80,111" },
    { name: "#Salaar", gross: "1,20,94,121" },
  ];

  return (
    <div>
      <Navbar />
      <div className="container">
        <h1>Main Categories</h1>
        <div className="main-buttons">
          {mainCategories.map((category) => (
            <div key={category} className="main-button">
              <button
                onClick={() =>
                  setSelectedMainCategory(
                    selectedMainCategory === category ? null : category
                  )
                }
                className="category-btn"
              >
                {category}
              </button>
              {selectedMainCategory === category && (
                <div className="sub-buttons">
                  {subCategories[category]?.map((subCategory) => (
                    <button
                      key={subCategory.name}
                      onClick={() =>
                        setSelectedSubCategory(
                          selectedSubCategory === subCategory.name
                            ? null
                            : subCategory.name
                        )
                      }
                      className="sub-category-btn"
                    >
                      {subCategory.name}
                    </button>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>

        {selectedSubCategory === "Sub 1.1" && (
          <div className="grossers-content">
            <h2>Nandyal Town Top 5 Grossers</h2>
            <ul>
              {topGrossers.map((movie) => (
                <li key={movie.name}>
                  {movie.name}: <strong>{movie.gross}</strong>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default APTG;
