import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
    const [currentImageIndex, setCurrentImageIndex] = useState(0);
    const images = [
        require('./assets/AA1.jpg'),
        require('./assets/MB1.jpg'),
        require('./assets/NTR1.jpg'),
        require('./assets/PB1.jpg'),
        require('./assets/PK1.jpg')
    ];

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
        }, 5000);

        return () => clearInterval(interval);
    }, [images.length]);

    return (
        <div className="App">
            {/* Navigation Bar */}
            <nav>
                <ul>
                    <li><a href="#home">Home</a></li>
                    <li><a href="#movies">Movies</a></li>
                    <li><a href="#news">News</a></li>
                    <li><a href="#contact">Contact</a></li>
                </ul>
            </nav>

            {/* Image Slide Show Section */}
            <section id="image-gallery">
                <div className="image-slide-show">
                    <img src={images[currentImageIndex]} alt={`Movie Image ${currentImageIndex + 1}`} />
                    <div className="news-blocks">
                        <div className="news-left">
                            <h3>News Title Left 1</h3>
                            <p>Short description of the news...</p>
                        </div>
                        <div className="news-right">
                            <h3>News Title Right 1</h3>
                            <p>Short description of the news...</p>
                        </div>
                    </div>
                </div>
            </section>

            {/* Remaining News Blocks Section */}
            <section id="news">
                <div className="news-container">
                    <div className="news-block">
                        <h3>News Title 2</h3>
                        <p>Short description of the news...</p>
                    </div>
                    <div className="news-block">
                        <h3>News Title 3</h3>
                        <p>Short description of the news...</p>
                    </div>
                    <div className="news-block">
                        <h3>News Title 4</h3>
                        <p>Short description of the news...</p>
                    </div>
                    <div className="news-block">
                        <h3>News Title 5</h3>
                        <p>Short description of the news...</p>
                    </div>
                </div>
            </section>
        </div>
    );
}

export default App;
