import React, { useState } from "react";
import Navbar from "../components/Navbar"; // Assuming you have a Navbar component
import "./APTG.css"; // Custom styling

function APTG() {
  const [selectedCategory, setSelectedCategory] = useState(""); // Track the selected category

  // Data for categories
  const categories = {
    "Anantapur Town Day 1 All Time Records": [
      "Simha  10.63L",
      "Orange   11.65L",
      "Dookudu  14.32L",
      "Oosaravelli   17.45L",
      "Baahubali  23.74L",
      "SGS  28.63L",
      "Khaidino150   29.82L",
      "Baahubali2   53.3L",
      "RRR  85.26L",
      "Devara   92.94L",
    ],
    "Guntakal Town Top Grossers": [
      "RRR  1.31Cr",
      "Baahubali2   90.8L",
      "Baahubali   63.4L",
      "Pushpa2   63L+(17D)",
      "Devara   60L",
      "Rangasthalam  58.5L",
      "Akhanda   56.6L",
      "SyeRaa  51.6L",
      "AVPL   50.5L",
      "Pushpa   49.2L",
    ],
    // Add other categories as needed...
  };

  const categoryKeys = Object.keys(categories);

  return (
    <div className="aptg-container">
      <Navbar />
      <h1 className="title">APTG Records</h1>
      <div className="buttons-container">
        {categoryKeys.slice(0, 8).map((category, index) => (
          <button
            key={index}
            className="category-button"
            onClick={() => setSelectedCategory(category)}
          >
            {category}
          </button>
        ))}
      </div>

      {/* Display selected category data */}
      <div className="data-display">
        {selectedCategory && (
          <>
            <h2 className="category-title">{selectedCategory}</h2>
            <ul className="data-list">
              {categories[selectedCategory].map((record, index) => (
                <li key={index} className="data-item">
                  {record}
                </li>
              ))}
            </ul>
          </>
        )}
      </div>
    </div>
  );
}

export default APTG;
