import React, { useState } from "react";
import Navbar from "../components/Navbar";
import "./APTG.css";

function APTG() {
  const [selectedMainCategory, setSelectedMainCategory] = useState(null);

  const mainCategories = [
    "Category 1",
    "Category 2",
    "Category 3",
    "Category 4",
    "Category 5",
    "Category 6",
    "Category 7",
    "Category 8",
  ];

  const subCategories = {
    "Category 1": ["Sub 1.1", "Sub 1.2", "Sub 1.3", "Sub 1.4", "Sub 1.5"],
    "Category 2": ["Sub 2.1", "Sub 2.2", "Sub 2.3", "Sub 2.4", "Sub 2.5"],
    "Category 3": ["Sub 3.1", "Sub 3.2", "Sub 3.3", "Sub 3.4", "Sub 3.5"],
    "Category 4": ["Sub 4.1", "Sub 4.2", "Sub 4.3", "Sub 4.4", "Sub 4.5"],
    "Category 5": ["Sub 5.1", "Sub 5.2", "Sub 5.3", "Sub 5.4", "Sub 5.5"],
    "Category 6": ["Sub 6.1", "Sub 6.2", "Sub 6.3", "Sub 6.4", "Sub 6.5"],
    "Category 7": ["Sub 7.1", "Sub 7.2", "Sub 7.3", "Sub 7.4", "Sub 7.5"],
    "Category 8": ["Sub 8.1", "Sub 8.2", "Sub 8.3", "Sub 8.4", "Sub 8.5"],
  };

  return (
    <div>
      <Navbar />
      <div className="container">
        <h1>Main Categories</h1>
        <div className="main-buttons">
          {mainCategories.map((category) => (
            <div key={category} className="main-button">
              <button
                onClick={() =>
                  setSelectedMainCategory(
                    selectedMainCategory === category ? null : category
                  )
                }
                className="category-btn"
              >
                {category}
              </button>
              {selectedMainCategory === category && (
                <div className="sub-buttons">
                  {subCategories[category].map((subCategory) => (
                    <button key={subCategory} className="sub-category-btn">
                      {subCategory}
                    </button>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default APTG;
