import React, { useEffect, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Navbar.css';

function Navbar() {
  const location = useLocation();
  const [activeTab, setActiveTab] = useState(location.pathname);

  // Update activeTab when the location changes
  useEffect(() => {
    setActiveTab(location.pathname);
  }, [location]);

  const tabs = [
    { path: '/', label: 'Home' },
    { path: '/PreReleaseBusiness', label: 'Pre-Release Business' },
    { path: '/Day1', label: 'Day 1' },
    { path: '/WW', label: 'WW Closing' },
    { path: '/boxoffice', label: 'BoxOffice' },
    { path: '/aptg', label: 'APTG Area Records' },
    { path: '/ATRDay1', label: 'ATR 9/9 RECORDS' },
  ];

  return (
    <div>
      <nav className="navbar">
        {/* Logo Section */}
        <div className="logo">
          <Link to="/" onClick={() => setActiveTab('/')}>
            తెలుగు CINEMA
          </Link>
        </div>

        {/* Navigation Links */}
        <ul className="nav-links">
          {tabs.map(({ path, label }) => (
            <li
              key={path}
              className={activeTab === path ? 'active-tab' : ''}
            >
              <Link to={path} onClick={() => setActiveTab(path)}>
                {label}
              </Link>
            </li>
          ))}
        </ul>
      </nav>
    </div>
  );
}

export default Navbar;
