import React, { useState } from "react";
import Navbar from "../components/Navbar";
import "./ATRDay1.css";

function ATRDay1() {
  const [selectedCategory, setSelectedCategory] = useState(""); // Track selected category

  const categories = {
    "MAHESH BABU":
    [
      "Samba 2004 2.55L",
      "Gudumba Shankar 2004 2.61L"
    ],
    "NTR":
    [
      "Samba 2004 2.55L",
      "Gudumba Shankar 2004 2.61L"
    ],
    "PK":
    [
      "Samba 2004 2.55L",
      "Gudumba Shankar 2004 2.61L"
    ],
    "PRABHAS":
    [
      "Samba 2004 2.55L",
      "Gudumba Shankar 2004 2.61L"
    ],
    "NTR":
    [
      "Samba 2004 2.55L",
      "Gudumba Shankar 2004 2.61L"
    ], "NTR":
    [
      "Samba 2004 2.55L",
      "Gudumba Shankar 2004 2.61L"
    ]



  };

  const handleSelection = (event) => {
    setSelectedCategory(event.target.value);
  };

  return (
    <div>
      <Navbar />
      <div className="movies-container">
        <div className="dropdown-container">
          <label htmlFor="category-select" className="dropdown-label">
            Select a category:
          </label>
          <select
            id="category-select"
            className="dropdown-select"
            value={selectedCategory}
            onChange={handleSelection}
          >
            <option value="" disabled>
              -- Choose an option --
            </option>
            {Object.keys(categories).map((category) => (
              <option key={category} value={category}>
                {category}
              </option>
            ))}
          </select>
        </div>

        {selectedCategory && (
          <div className="records-table">
            <h2>{selectedCategory}</h2>
            <p>Box Office data is compiled from various sources.</p>
            <table>
              <thead>
                <tr>
                  <th>#</th>
                  <th>Movie</th>
                  <th>Year</th>
                  <th>Collection</th>
                </tr>
              </thead>
              <tbody>
                {categories[selectedCategory].map((movie, index) => {
                  const parts = movie.split(" "); // Splitting movie details
                  const title = parts.slice(0, -2).join(" "); // Extracting title
                  const year = parts[parts.length - 2]; // Extracting year
                  const collection = parts[parts.length - 1]; // Extracting collection

                  return (
                    <tr key={index}>
                      <td>{index + 1}</td>
                      <td>
                        <div className="multi-line-content">
                          {title}
                        </div>
                      </td>
                      <td>{year}</td>
                      <td>{collection}</td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

export default ATRDay1;
