import React, { useEffect, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Navbar.css';

function Navbar() {
  const location = useLocation();
  const [activeTab, setActiveTab] = useState(location.pathname);

  // Sync active tab with URL changes
  useEffect(() => {
    setActiveTab(location.pathname);
  }, [location]);

  const tabs = [
    { path: '/', label: 'Home' },
    { path: '/PreReleaseBusiness', label: 'Pre-Release Business' },
    { path: '/Day1', label: 'Day 1' },
    { path: '/WW', label: 'WW Closing' },
    { path: '/boxoffice', label: 'BoxOffice' },
    { path: '/aptg', label: 'APTG Area Records' },
    { path: '/ATRDay1', label: 'ATR 9/9 RECORDS' },
  ];

  return (
    <nav className="navbar">
      <div className="logo">
        <Link to="/" onClick={() => setActiveTab('/')}>
          తెలుగు CINEMA
        </Link>
      </div>

      <ul className="nav-links">
        {tabs.map(({ path, label }) => (
          <li key={path}>
            <Link
              to={path}
              className={`nav-button ${activeTab === path ? 'active-tab' : ''}`}
              onClick={() => setActiveTab(path)}
            >
              {label}
            </Link>
          </li>
        ))}
      </ul>
    </nav>
  );
}

export default Navbar;
