import React, { useState } from 'react';
import './Navbar.css';

function Navbar() {
  const [activeTab, setActiveTab] = useState('Home'); // State to track active tab

  const renderContent = () => {
    switch (activeTab) {
      case 'Home':
        return <p>Welcome to Telugu Cinema - Discover the latest in Telugu movies.</p>;
      case 'Movies':
        return <p>Explore upcoming releases, reviews, and box office updates.</p>;
      case 'News':
        return <p>Stay updated with the latest news and happenings in Telugu cinema.</p>;
      case 'Contact':
        return <p>Reach out to us for inquiries or collaborations.</p>;
      default:
        return null;
    }
  };

  return (
    <div>
      <nav className="navbar">
        <div className="logo">
          <a href="/">Telugu Cinema</a>
        </div>
        <ul className="nav-links">
          {['Home', 'Movies', 'News', 'Contact'].map((tab) => (
            <li
              key={tab}
              className={activeTab === tab ? 'active-tab' : ''}
              onClick={() => setActiveTab(tab)}
            >
              <a href={`#${tab.toLowerCase()}`}>{tab}</a>
            </li>
          ))}
        </ul>
      </nav>
      <div className="tab-content">{renderContent()}</div>
    </div>
  );
}

export default Navbar;
