import React, { useEffect, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Navbar.css';

function Navbar() {
  const location = useLocation();
  const [activeTab, setActiveTab] = useState(location.pathname);

  // Sync active tab with URL changes
  useEffect(() => {
    setActiveTab(location.pathname);
  }, [location]);

  const tabs = [
    { path: '/', label: 'Home' },
    { path: '/PreReleaseBusiness', label: 'Pre-Release Business' },
    { path: '/Day1', label: 'Day 1' },
    { path: '/WW', label: 'WW Closing' },
    { path: '/boxoffice', label: 'BoxOffice' },
    { path: '/aptg', label: 'APTG Area Records' },
    { path: '/ATRDay1', label: 'ATR 9/9 RECORDS' },
  ];

  return (
    <nav className="navbar">
      <div className="logo">
        <Link to="/" onClick={() => setActiveTab('/')}>
          తెలుగు CINEMA
        </Link>
      </div>
      <div className="button-container">
          <Link to="/Day1" className="nav-button creative-button">Day 1</Link>
          <Link to="/PreReleaseBusiness" className="nav-button creative-button">Pre-Release Business</Link>
          <Link to="/WW" className="nav-button creative-button">WW Closing</Link>
          <Link to="/boxoffice" className="nav-button creative-button">BoxOffice</Link>
          <Link to="/aptg" className="nav-button creative-button">APTG Area Records</Link>
          <Link to="/ATRDay1" className="nav-button creative-button">ATR 9/9 RECORDS</Link>
        </div>
      <ul className="nav-links">
        {tabs.map(({ path, label }) => (
          <li
            key={path}
            className={activeTab === path ? 'active-tab' : ''}
          >
            <Link to={path} onClick={() => setActiveTab(path)}>
              {label}
            </Link>
          </li>
        ))}
      </ul>
    </nav>
  );
}

export default Navbar;
