import React, { useState } from "react";
import Navbar from "../components/Navbar";
import "./APTG.css";

function APTG() {
  const [selectedMainCategory, setSelectedMainCategory] = useState(null);
  const [selectedSubCategory, setSelectedSubCategory] = useState(null);

  const mainCategories = [
    "Nizam",
    "Ceeded",
    "Nellore",
    "Guntur",
    "Krishna",
    "UA",
    "East",
    "West",
  ];

  const subCategories = {
    "Nizam": [
      { name: "Nizamabad Town Top 10 Grossers", content: "Nizamabad Town Top 10 Grossers" },
      { name: " ", content: " " },
      { name: " ", content: " " },
      { name: " ", content: " " },
    ],
    "Ceeded": [
      { name: "Anantapur Town Day 1", content: "Anantapur Town Day 1" },
      { name: "Nandyal Town Day 1", content: "Nandyal Town Day 1" },
      { name: "Kadapa Town Top 5 Grossers", content: "Kadapa Town Top 5 Grossers" },
      { name: "Adoni Town Top Grossers", content: "Adoni Town Top Grossers" },
    ],
    "Guntur": [
      { name: "Macherla Town Day 1 Top 10 Grossers", content: "Macherla Town Day 1 Top 10 Grossers" },
      { name: "Chirala Town 70L+ Grossers", content: "Chirala Town 70L+ Grossers" },
    ],
    // Add other categories similarly...
  };

  const topGrossers = {
    "Macherla Town Day 1 Top 10 Grossers": [
      { "name": "#GameChanger", "gross": "12.5L" },
      { "name": "#Pushpa2", "gross": "12.17L" },
      { "name": "#RRR", "gross": "11.25L" },
    ],
    "Anantapur Town Day 1": [
      { "name": "#Simha", "gross": "10.63L" },
      { "name": "#Orange", "gross": "11.65L" },
    ],
    // Add other grossers similarly...
  };

  const handleMainCategoryChange = (event) => {
    setSelectedMainCategory(event.target.value);
    setSelectedSubCategory(null); // Reset sub-category when main category changes
  };

  const handleSubCategoryChange = (event) => {
    setSelectedSubCategory(event.target.value);
  };

  return (
    <div className="apgt-container">
      <Navbar />
      <div className="category-selection">
        <label>Select Main Category:</label>
        <select value={selectedMainCategory} onChange={handleMainCategoryChange}>
          <option value="">--Select--</option>
          {mainCategories.map((category) => (
            <option key={category} value={category}>
              {category}
            </option>
          ))}
        </select>
      </div>

      {selectedMainCategory && (
        <div className="subcategory-selection">
          <label>Select Sub Category:</label>
          <select value={selectedSubCategory} onChange={handleSubCategoryChange}>
            <option value="">--Select--</option>
            {subCategories[selectedMainCategory]?.map((sub) => (
              <option key={sub.name} value={sub.name}>
                {sub.name}
              </option>
            ))}
          </select>
        </div>
      )}

      {selectedSubCategory && (
        <div className="grossers-list">
          <h3>{selectedSubCategory}</h3>
          <table>
            <thead>
              <tr>
                <th>Movie</th>
                <th>Gross</th>
              </tr>
            </thead>
            <tbody>
              {topGrossers[selectedSubCategory]?.map((gross) => (
                <tr key={gross.name}>
                  <td>{gross.name}</td>
                  <td>{gross.gross}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default APTG;
