import React, { useState } from "react";
import Navbar from "../components/Navbar";
import "./Movies.css";

function Movies() {
  const [selectedCategory, setSelectedCategory] = useState(""); // Track selected category

  const categories = {
    "Day 1 APTG": [
      "DOOKUDU 2011",
      "OOSARAVALLI 2011",
      "BUSINESSMAN 2012",
      "RACHA 2012",
      "DAMMU 2012",
      "GABBAR SINGH 2012",
      "CMGR 2012",
      "BADSHAH 2013",
      "ATTARINTIKI DAREDI 2013",
      "AGADU 2014",
      "BAAHUBALI 2015",
      "SRIMANTUDU * 2015",
      "SARDAR GABBAR SINGH * 2016",
      "K 150 * 2017",
      "JAI LAVA KUSA *Gross 2017",
      "BAAHUBALI 2 2017",
      "AGNATHAVASI * 2018",
      "ARAVINDA SAMETHA * 2018",
      "SAAHO * 2019",
      "SYE RAA * 2019",
      "RRR 2022",
      "SALAAR * 2023",
      "DEVARA * 2024",
      "PUSHPA 2 * 2024",
    ],
    "WW DAY 1": [],
    // Add more categories as needed
  };

  const handleSelection = (event) => {
    setSelectedCategory(event.target.value);
  };

  return (
    <div>
      <Navbar />
      <div className="movies-container">
        <div className="dropdown-container">
          <label htmlFor="category-select" className="dropdown-label">
            Select a category:
          </label>
          <select
            id="category-select"
            className="dropdown-select"
            value={selectedCategory}
            onChange={handleSelection}
          >
            <option value="" disabled>
              -- Choose an option --
            </option>
            {Object.keys(categories).map((category) => (
              <option key={category} value={category}>
                {category}
              </option>
            ))}
          </select>
        </div>

        {selectedCategory && (
          <div className="records-table">
            <h2>{selectedCategory}</h2>

            <table>
              <thead>
                <tr>
                  <th>#</th>
                  <th>Movie</th>
                  <th>Year</th>
                </tr>
              </thead>
              <tbody>
                {categories[selectedCategory].map((movie, index) => {
                  const [title, year] = movie.split(/(?=\d{4})/); // Splitting title and year
                  return (
                    <tr key={index}>
                      <td>{index + 1}</td>
                      <td>
                        <div className="multi-line-content">
                          {title.trim()}
                        </div>
                      </td>
                      <td>{year.trim()}</td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

export default Movies;
