import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import axios from 'axios';
import './Navbar.css';

function Navbar() {
  const location = useLocation();
  const [activeTab, setActiveTab] = useState(location.pathname);
  const [visitCount, setVisitCount] = useState(0);

  const handleTabClick = (tabPath) => {
    setActiveTab(tabPath);
  };

  // Fetch visit count from the backend
  useEffect(() => {
    axios
      .get('http://localhost:3000/increment-visit') // Increment visit on every load
      .then((response) => {
        setVisitCount(response.data.visitCount); // Update state with the visit count
      })
      .catch((error) => console.error('Error fetching visit count:', error));
  }, []);

  return (
    <div>
      <nav className="navbar">
        <div className="logo">
          <Link to="/" onClick={() => handleTabClick('/')}>
            తెలుగు CINEMA
          </Link>
        </div>

        <ul className="nav-links">
          {['/', '/PreReleaseBusiness', '/Day1', '/WW', '/boxoffice', '/aptg', '/ATRDay1', '/Contact'].map((path, index) => (
            <li
              key={path}
              className={activeTab === path ? 'active-tab' : ''}
              onClick={() => handleTabClick(path)}
            >
              <Link to={path}>
                {['Home', 'Pre-Release B', 'Day1', 'WW', 'Archives', 'APTG Area', 'RECORDS', 'Contact'][index]}
              </Link>
            </li>
          ))}
        </ul>

        {/* Display visit count in the navbar */}
        <div className="visit-counter">
          <span className="counter-badge">{visitCount}</span>
        </div>
      </nav>
    </div>
  );
}

export default Navbar;
