import React, { useState } from 'react';
import Navbar from './components/Navbar'; // Assuming Navbar is already created
import './Movies.css'; // Add your styles here

function Movies() {
  // State to track the selected option
  const [showDayOneRecords, setShowDayOneRecords] = useState(false);

  // Day 1 records data
  const dayOneRecords = [
    { name: "DOOKUDU", year: 2011 },
    { name: "OOSARAVALLI", year: 2011 },
    { name: "BUSINESSMAN", year: 2012 },
    { name: "RACHA", year: 2012 },
    { name: "DAMMU", year: 2012 },
    { name: "GABBAR SINGH", year: 2012 },
    { name: "CMGR", year: 2012 },
    { name: "BADSHAH", year: 2013 },
    { name: "ATTARINTIKI DAREDI", year: 2013 },
    { name: "AGADU", year: 2014 },
    { name: "BAAHUBALI", year: 2015 },
    { name: "SRIMANTUDU *", year: 2015 },
    { name: "SARDAR GABBAR SINGH *", year: 2016 },
    { name: "K 150 *", year: 2017 },
    { name: "JAI LAVA KUSA *Gross", year: 2017 },
    { name: "BAAHUBALI 2", year: 2017 },
    { name: "AGNATHAVASI *", year: 2018 },
    { name: "ARAVINDA SAMETHA *", year: 2018 },
    { name: "SAAHO *", year: 2019 },
    { name: "SYE RAA *", year: 2019 },
    { name: "RRR", year: 2022 },
    { name: "SALAAR *", year: 2023 },
    { name: "DEVARA *", year: 2024 },
    { name: "PUSHPA 2 *", year: 2024 },
  ];

  return (
    <div>
      <Navbar />
      <div className="movies-container">
        <h1>Movies - Day 1 Records (APTG)</h1>
        <button
          onClick={() => setShowDayOneRecords(!showDayOneRecords)}
          className="toggle-button"
        >
          {showDayOneRecords ? 'Hide Day 1 Records' : 'Show Day 1 Records'}
        </button>

        {showDayOneRecords && (
          <div className="day-one-records">
            <h2>Day 1 Records:</h2>
            <ul>
              {dayOneRecords.map((movie, index) => (
                <li key={index}>
                  {index + 1}. {movie.name} - {movie.year}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default Movies;
