import React, { useState } from "react";
import Navbar from "../components/Navbar";
import "./Movies.css";

function Movies() {
  const [selectedCategory, setSelectedCategory] = useState("");

  const categories = {
    "Day 1 APTG": [
      "DOOKUDU 2011",
      "OOSARAVALLI 2011",
      "BUSINESSMAN 2012",
      "RRR 2022",
      "PUSHPA 2 * 2024",
    ],
    "WW APTG": [
      "BAAHUBALI 2015",
      "SRIMANTUDU 2015",
      "RRR 2022",
      "SALAAR 2023",
      "DEVARA 2024",
    ],
    "Day 1 Ceded": [
      "AGADU 2014",
      "JAI LAVA KUSA 2017",
      "ARAVINDA SAMETHA 2018",
      "SAAHO 2019",
      "SYE RAA 2019",
    ],
  };

  return (
    <div>
      <Navbar />
      <div className="movies-container">
        <ul className="categories-list">
          {Object.keys(categories).map((category) => (
            <li
              key={category}
              className="category-item"
              onClick={() => setSelectedCategory(category)}
            >
              {category}
            </li>
          ))}
        </ul>

        {selectedCategory && (
          <div className="records-table">
            <h2>{selectedCategory}</h2>
            <table>
              <thead>
                <tr>
                  <th>#</th>
                  <th>Movie</th>
                </tr>
              </thead>
              <tbody>
                {categories[selectedCategory].map((movie, index) => (
                  <tr key={index}>
                    <td>{index + 1}</td>
                    <td>{movie}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

export default Movies;
